# Data: 2026-01-15

Write-Host "ğŸ¨ Instalando tema Oh My Posh personalizado..." -ForegroundColor Cyan

# Verificar se Oh My Posh estÃ¡ instalado
Write-Host "ğŸ” Verificando instalaÃ§Ã£o do Oh My Posh..." -ForegroundColor Yellow
$ohMyPoshInstalled = Get-Command oh-my-posh -ErrorAction SilentlyContinue

if (-not $ohMyPoshInstalled) {
    Write-Host "âŒ Oh My Posh nÃ£o estÃ¡ instalado!" -ForegroundColor Red
    Write-Host "ğŸ“¦ Instalando Oh My Posh via winget..." -ForegroundColor Yellow
    
    try {
        winget install JanDeDobbeleer.OhMyPosh -s winget --accept-package-agreements --accept-source-agreements
        
        # Atualizar PATH para a sessÃ£o atual
        $env:Path = [System.Environment]::GetEnvironmentVariable("Path","Machine") + ";" + [System.Environment]::GetEnvironmentVariable("Path","User")
        
        Write-Host "âœ… Oh My Posh instalado com sucesso!" -ForegroundColor Green
        Write-Host "ğŸ’¡ VocÃª pode precisar reiniciar o terminal para usar o Oh My Posh" -ForegroundColor Cyan
    } catch {
        Write-Host "âŒ Erro ao instalar Oh My Posh: $_" -ForegroundColor Red
        Write-Host "ğŸ’¡ Tente instalar manualmente: winget install JanDeDobbeleer.OhMyPosh" -ForegroundColor Yellow
        exit 1
    }
} else {
    Write-Host "âœ… Oh My Posh jÃ¡ estÃ¡ instalado" -ForegroundColor Green
    Write-Host "ğŸ”„ Atualizando Oh My Posh..." -ForegroundColor Yellow
    
    try {
        oh-my-posh upgrade --force
        Write-Host "âœ… Oh My Posh atualizado com sucesso!" -ForegroundColor Green
    } catch {
        Write-Host "âš ï¸  NÃ£o foi possÃ­vel atualizar, mas continuando com a versÃ£o atual" -ForegroundColor Yellow
    }
}

# URL do tema
$themeUrl = "https://raw.githubusercontent.com/pdmartins/scripts/refs/heads/main/oh-my-posh/blocks.emoji.omp.json"

# DiretÃ³rio de temas do Oh My Posh
$themesPath = "$env:POSH_THEMES_PATH"
if (-not $themesPath) {
    $themesPath = "$env:LOCALAPPDATA\Programs\oh-my-posh\themes"
}

# Nome do arquivo do tema
$themeName = "blocks.emoji.omp.json"
$themeFilePath = Join-Path $themesPath $themeName

Write-Host "ğŸ“ DiretÃ³rio de temas: $themesPath" -ForegroundColor Yellow

# Criar diretÃ³rio se nÃ£o existir
if (-not (Test-Path $themesPath)) {
    Write-Host "ğŸ“‚ Criando diretÃ³rio de temas..." -ForegroundColor Yellow
    New-Item -ItemType Directory -Path $themesPath -Force | Out-Null
}

# Baixar o tema
try {
    Write-Host "â¬‡ï¸  Baixando tema do Gist..." -ForegroundColor Yellow
    Invoke-WebRequest -Uri $themeUrl -OutFile $themeFilePath -UseBasicParsing
    Write-Host "âœ… Tema baixado com sucesso: $themeFilePath" -ForegroundColor Green
} catch {
    Write-Host "âŒ Erro ao baixar o tema: $_" -ForegroundColor Red
    exit 1
}

# Verificar se o profile existe
if (-not (Test-Path $PROFILE)) {
    Write-Host "ğŸ“ Criando arquivo de profile..." -ForegroundColor Yellow
    New-Item -Path $PROFILE -Type File -Force | Out-Null
    $profileContent = ""
} else {
    # Ler o conteÃºdo atual do profile
    $profileContent = Get-Content $PROFILE -Raw -ErrorAction SilentlyContinue
    if (-not $profileContent) {
        $profileContent = ""
    }
}

# Verificar se jÃ¡ existe alguma linha oh-my-posh init no profile
$ohMyPoshPattern = "oh-my-posh\s+init"
$newInitLine = "oh-my-posh init pwsh --config '$themeFilePath' | Invoke-Expression"

if ($profileContent -match $ohMyPoshPattern) {
    Write-Host "ğŸ”„ Atualizando configuraÃ§Ã£o existente do Oh My Posh no profile..." -ForegroundColor Yellow
    
    # Substituir qualquer linha que contenha "oh-my-posh init"
    $profileContent = $profileContent -replace ".*oh-my-posh\s+init[^\r\n]*", $newInitLine
    
    Set-Content $PROFILE -Value $profileContent -Encoding UTF8
    Write-Host "âœ… ConfiguraÃ§Ã£o do Oh My Posh atualizada no profile" -ForegroundColor Green
} else {
    Write-Host "â• Adicionando Oh My Posh ao profile..." -ForegroundColor Yellow
    
    # Adicionar no inÃ­cio do profile se nÃ£o estiver vazio, senÃ£o criar
    if ($profileContent.Trim()) {
        $profileContent = $newInitLine + "`r`n`r`n" + $profileContent
    } else {
        $profileContent = $newInitLine + "`r`n"
    }
    
    Set-Content $PROFILE -Value $profileContent -Encoding UTF8
    Write-Host "âœ… Oh My Posh adicionado ao profile" -ForegroundColor Green
}

Write-Host "`nâœ¨ InstalaÃ§Ã£o concluÃ­da!" -ForegroundColor Green
Write-Host "ğŸ“‹ Para aplicar as mudanÃ§as, execute:" -ForegroundColor Cyan
Write-Host "   . `$PROFILE" -ForegroundColor White
Write-Host "`nğŸ’¡ Ou feche e reabra o PowerShell" -ForegroundColor Cyan

