# FunÃ§Ã£o para formatar tempo restante de forma dinÃ¢mica
function Format-TimeRemaining {
    param([double]$Seconds)

    if ($Seconds -lt 60) {
        return "   ~{0}s" -f [math]::Round($Seconds)
    }
    elseif ($Seconds -lt 3600) {
        return " ~{0}min" -f [math]::Ceiling($Seconds / 60)
    }
    elseif ($Seconds -lt 86400) {
        $hours = [math]::Floor($Seconds / 3600)
        $mins = [math]::Round(($Seconds % 3600) / 60)
        return " ~{0}h{1:D2}" -f $hours, $mins
    }
    else {
        $days = [math]::Floor($Seconds / 86400)
        $hours = [math]::Round(($Seconds % 86400) / 3600)
        return "~{0}d{1}h" -f $days, $hours
    }
}

