# Main
# ============================================================================

function Main {
    Write-Step "Clone de RepositÃ³rios Azure DevOps"
    Write-Host ""

    # Tentar carregar config se parÃ¢metros nÃ£o foram fornecidos
    if ([string]::IsNullOrEmpty($OrganizationUrl)) {
        if (-not [string]::IsNullOrEmpty($ConfigFile)) {
            Import-ConfigFile -Path $ConfigFile | Out-Null
        }
        elseif (Test-Path $DefaultConfigFile) {
            Import-ConfigFile -Path $DefaultConfigFile | Out-Null
        }
    }

    Test-Prerequisites
    Test-Configuration

    # Criar pasta de destino
    if (-not (Test-Path $ClonePath)) {
        Write-Info "Criando pasta: $ClonePath"
        New-Item -ItemType Directory -Path $ClonePath -Force | Out-Null
    }

    # Buscar repositÃ³rios
    $repos = Get-Repositories

    if ($repos.Count -eq 0) {
        Write-Warning "Nenhum repositÃ³rio encontrado no projeto"
        exit 0
    }

    # Inicializar progresso
    $script:TotalRepos = $repos.Count
    $script:CurrentRepo = 0
    $script:StartTime = Get-Date

    Write-Host ""
    Write-Host "ğŸ“‹ Total: $($script:TotalRepos) repositÃ³rios | â±ï¸ InÃ­cio: $($script:StartTime.ToString('HH:mm'))" -ForegroundColor Cyan
    Write-Host ""

    foreach ($repo in $repos) {
        if ($script:Cancelled) {
            Write-Warning "Cancelado pelo usuÃ¡rio"
            break
        }
        Copy-Repository -RepoName $repo.name -RepoUrl $repo.remoteUrl | Out-Null

        # Exibir previsÃ£o de tÃ©rmino apÃ³s primeiros repos (quando jÃ¡ hÃ¡ dados)
        if ($script:CurrentRepo -eq 5 -and $script:EstimatedFinish) {
            Write-Host ""
            Write-Host "â±ï¸ PrevisÃ£o de tÃ©rmino: $($script:EstimatedFinish)" -ForegroundColor Cyan
            Write-Host ""
        }
    }

    Write-Summary
}

try {
    Main
}
finally {
    # Limpar event handler
    Get-EventSubscriber -SourceIdentifier PowerShell.Exiting -ErrorAction SilentlyContinue | Unregister-Event -ErrorAction SilentlyContinue
}

