# Script: clone-devops-repos.ps1
# Description: Clone all repositories from an Azure DevOps project
# ============================================================================

param(
    [string]$OrganizationUrl,
    [string]$Project,
    [string]$Username,
    [string]$Pat,
    [string]$ClonePath,
    [string]$ConfigFile
)

# ============================================================================
# Helper Functions
# ============================================================================

function Write-Step {
    param([string]$Message)
    Write-Host "ğŸš€ $Message" -ForegroundColor White
}

function Write-Info {
    param([string]$Message)
    Write-Host "ğŸ” $Message" -ForegroundColor Cyan
}

function Write-Success {
    param([string]$Message)
    Write-Host "âœ… $Message" -ForegroundColor Green
}

function Write-Warning {
    param([string]$Message)
    Write-Host "âš ï¸ $Message" -ForegroundColor Yellow
}

function Write-Error {
    param([string]$Message)
    Write-Host "âŒ $Message" -ForegroundColor Red
}

function Write-Install {
    param([string]$Message)
    Write-Host "ğŸ“¦ $Message" -ForegroundColor Yellow
}

function Write-Update {
    param([string]$Message)
    Write-Host "ğŸ”„ $Message" -ForegroundColor Cyan
}

# Script directory
$ScriptDir = Split-Path -Parent $MyInvocation.MyCommand.Path
$DefaultConfigFile = Join-Path $ScriptDir "config.json"

# Tracking arrays
$script:ReposCloned = @()
$script:ReposUpdated = @()
$script:ReposStashed = @()
$script:ReposFailed = @()
$script:Cancelled = $false

# Progress tracking
$script:TotalRepos = 0
$script:CurrentRepo = 0
$script:StartTime = $null
$script:RepoTimes = @()
$script:EstimatedFinish = ""

# Format time remaining dynamically
function Format-TimeRemaining {
    param([double]$Seconds)

    if ($Seconds -lt 60) {
        return "   ~{0}s" -f [math]::Round($Seconds)
    }
    elseif ($Seconds -lt 3600) {
        return " ~{0}min" -f [math]::Ceiling($Seconds / 60)
    }
    elseif ($Seconds -lt 86400) {
        $hours = [math]::Floor($Seconds / 3600)
        $mins = [math]::Round(($Seconds % 3600) / 60)
        return " ~{0}h{1:D2}" -f $hours, $mins
    }
    else {
        $days = [math]::Floor($Seconds / 86400)
        $hours = [math]::Round(($Seconds % 86400) / 3600)
        return "~{0}d{1}h" -f $days, $hours
    }
}

# Handler for Ctrl+C
$null = Register-EngineEvent -SourceIdentifier PowerShell.Exiting -Action {
    $script:Cancelled = $true
}

trap {
    Write-Host ""
    Write-Warning "Operation cancelled by user"
    $script:Cancelled = $true
    break
}

# ============================================================================
# Functions
