        }
        Write-Host ""
    }

    # Stashed
    if ($script:ReposStashed.Count -gt 0) {
        Write-Warning "Repositories with stash applied ($($script:ReposStashed.Count)):"
        foreach ($repo in $script:ReposStashed) {
