function Show-Usage {
    Write-Host "Uso: .\clone-devops-repos.ps1 [opÃ§Ãµes]"
    Write-Host ""
    Write-Host "OpÃ§Ãµes:"
    Write-Host "  -OrganizationUrl URL     URL da organizaÃ§Ã£o Azure DevOps"
    Write-Host "  -Project NOME            Nome do projeto"
    Write-Host "  -Username USER           Nome de usuÃ¡rio"
    Write-Host "  -Pat TOKEN               Personal Access Token"
    Write-Host "  -ClonePath PATH          Pasta de destino (padrÃ£o: .\repos)"
    Write-Host "  -ConfigFile FILE         Arquivo de configuraÃ§Ã£o JSON"
    Write-Host ""
    Write-Host "Exemplo de config (config.json):"
    Write-Host '{'
