    }

    # Totalizador
    Write-Host "------------------------------------------------------------"
    $total = $script:ReposCloned.Count + $script:ReposUpdated.Count + $script:ReposFailed.Count
    $success = $script:ReposCloned.Count + $script:ReposUpdated.Count

    # Tempo de execuÃ§Ã£o
    $elapsed = (Get-Date) - $script:StartTime
    $elapsedFormatted = ""
    if ($elapsed.TotalSeconds -lt 60) {
        $elapsedFormatted = "{0}s" -f [math]::Round($elapsed.TotalSeconds)
    } elseif ($elapsed.TotalMinutes -lt 60) {
        $elapsedFormatted = "{0}min {1}s" -f [math]::Floor($elapsed.TotalMinutes), $elapsed.Seconds
    } else {
        $elapsedFormatted = "{0}h {1}min" -f [math]::Floor($elapsed.TotalHours), $elapsed.Minutes
    }

    Write-Host "ğŸ“Š TOTAL: $success de $total repositÃ³rios processados com sucesso" -ForegroundColor White
    Write-Host "   ğŸ“¦ Clonados:    $($script:ReposCloned.Count)"
    Write-Host "   ğŸ”„ Atualizados: $($script:ReposUpdated.Count)"
    Write-Host "   ğŸ“‚ Com stash:   $($script:ReposStashed.Count)"
    Write-Host "   âŒ Falhas:      $($script:ReposFailed.Count)"
    Write-Host "   â±ï¸ Tempo:       $elapsedFormatted"
    Write-Host "------------------------------------------------------------"
    Write-Info "Local: $ClonePath"
}

# ============================================================================
# Main
# ============================================================================

function Main {
    Write-Step "Clone de RepositÃ³rios Azure DevOps"
    Write-Host ""

    # Tentar carregar config se parÃ¢metros nÃ£o foram fornecidos
    if ([string]::IsNullOrEmpty($OrganizationUrl)) {
        if (-not [string]::IsNullOrEmpty($ConfigFile)) {
            Import-ConfigFile -Path $ConfigFile | Out-Null
        }
        elseif (Test-Path $DefaultConfigFile) {
            Import-ConfigFile -Path $DefaultConfigFile | Out-Null
        }
    }

    Test-Prerequisites
    Test-Configuration

    # Criar pasta de destino
    if (-not (Test-Path $ClonePath)) {
        Write-Info "Criando pasta: $ClonePath"
        New-Item -ItemType Directory -Path $ClonePath -Force | Out-Null
    }

    # Buscar repositÃ³rios
    $repos = Get-Repositories

    if ($repos.Count -eq 0) {
        Write-Warning "Nenhum repositÃ³rio encontrado no projeto"
        exit 0
    }

    # Inicializar progresso
    $script:TotalRepos = $repos.Count
    $script:CurrentRepo = 0
    $script:StartTime = Get-Date

    Write-Host ""
    Write-Host "ğŸ“‹ Total: $($script:TotalRepos) repositÃ³rios | â±ï¸ InÃ­cio: $($script:StartTime.ToString('HH:mm')) | â³=tempo restante | ğŸ=previsÃ£o tÃ©rmino" -ForegroundColor Cyan
    Write-Host ""

    foreach ($repo in $repos) {
        if ($script:Cancelled) {
