# ============================================================================
# Script: clone-devops-repos.ps1
# Description: Clona todos os reposit√≥rios de um projeto no Azure DevOps
# ============================================================================

param(
    [string]$OrganizationUrl,
    [string]$Project,
    [string]$Username,
    [string]$Pat,
    [string]$ClonePath,
    [string]$ConfigFile
)

# ============================================================================
# Helper Functions
# ============================================================================

function Write-Step {
    param([string]$Message)
    Write-Host "üöÄ $Message" -ForegroundColor White
}

function Write-Info {
    param([string]$Message)
    Write-Host "üîç $Message" -ForegroundColor Cyan
}

function Write-Success {
    param([string]$Message)
    Write-Host "‚úÖ $Message" -ForegroundColor Green
}

function Write-Warning {
    param([string]$Message)
    Write-Host "‚ö†Ô∏è $Message" -ForegroundColor Yellow
}

function Write-Error {
    param([string]$Message)
    Write-Host "‚ùå $Message" -ForegroundColor Red
}

function Write-Install {
    param([string]$Message)
    Write-Host "üì¶ $Message" -ForegroundColor Yellow
}

function Write-Update {
    param([string]$Message)
    Write-Host "üîÑ $Message" -ForegroundColor Cyan
}

# Script directory
$ScriptDir = Split-Path -Parent $MyInvocation.MyCommand.Path
$DefaultConfigFile = Join-Path $ScriptDir "config.json"

# Tracking arrays
$script:ReposCloned = @()
$script:ReposUpdated = @()
$script:ReposStashed = @()
$script:ReposFailed = @()

# ============================================================================
# Functions
# ============================================================================

function Show-Usage {
    Write-Host "Uso: .\clone-devops-repos.ps1 [op√ß√µes]"
    Write-Host ""
    Write-Host "Op√ß√µes:"
    Write-Host "  -OrganizationUrl URL     URL da organiza√ß√£o Azure DevOps"
    Write-Host "  -Project NOME            Nome do projeto"
    Write-Host "  -Username USER           Nome de usu√°rio"
    Write-Host "  -Pat TOKEN               Personal Access Token"
    Write-Host "  -ClonePath PATH          Pasta de destino (padr√£o: .\repos)"
    Write-Host "  -ConfigFile FILE         Arquivo de configura√ß√£o JSON"
    Write-Host ""
    Write-Host "Exemplo de config (config.json):"
    Write-Host '{'
    Write-Host '  "organization_url": "https://dev.azure.com/sua-org",'
    Write-Host '  "project": "nome-do-projeto",'
    Write-Host '  "username": "seu-usuario",'
    Write-Host '  "pat": "seu-personal-access-token",'
    Write-Host '  "clone_path": "./repos"'
    Write-Host '}'
}

function Import-ConfigFile {
    param([string]$Path)

    if (-not (Test-Path $Path)) {
        return $false
    }

    Write-Info "Carregando configura√ß√£o de: $Path"

    try {
        $config = Get-Content $Path -Raw | ConvertFrom-Json

        if ($config.organization_url) { $script:OrganizationUrl = $config.organization_url }
        if ($config.project) { $script:Project = $config.project }
        if ($config.username) { $script:Username = $config.username }
        if ($config.pat) { $script:Pat = $config.pat }
        if ($config.clone_path) { $script:ClonePath = $config.clone_path }

        Write-Success "Configura√ß√£o carregada"
        return $true
    }
    catch {
        Write-Error "Erro ao ler configura√ß√£o: $_"
        return $false
    }
}

function Test-Prerequisites {
    Write-Info "Verificando pr√©-requisitos..."

    if (-not (Get-Command "git" -ErrorAction SilentlyContinue)) {
        Write-Error "Git n√£o encontrado. Instale o Git primeiro."
        exit 1
    }

    Write-Success "Pr√©-requisitos OK"
}

function Test-Configuration {
    $valid = $true

    if ([string]::IsNullOrEmpty($OrganizationUrl)) {
        Write-Error "URL da organiza√ß√£o n√£o informada"
        $valid = $false
    }

    if ([string]::IsNullOrEmpty($Project)) {
        Write-Error "Nome do projeto n√£o informado"
        $valid = $false
    }

    if ([string]::IsNullOrEmpty($Username)) {
        Write-Error "Username n√£o informado"
        $valid = $false
    }

    if ([string]::IsNullOrEmpty($Pat)) {
        Write-Error "PAT (Personal Access Token) n√£o informado"
        $valid = $false
    }

    if (-not $valid) {
        Write-Host ""
        Show-Usage
        exit 1
    }

    # Valor padr√£o para ClonePath
    if ([string]::IsNullOrEmpty($script:ClonePath)) {
        $script:ClonePath = ".\repos"
    }
}

function Get-Repositories {
    Write-Info "Buscando reposit√≥rios do projeto: $Project"

    # Remover trailing slash da URL
    $orgUrl = $OrganizationUrl.TrimEnd('/')

    # Codificar credenciais em base64
    $auth = [Convert]::ToBase64String([Text.Encoding]::ASCII.GetBytes("${Username}:${Pat}"))

    # URL da API
    $apiUrl = "${orgUrl}/${Project}/_apis/git/repositories?api-version=7.0"

    try {
        $headers = @{
            "Authorization" = "Basic $auth"
        }

        $response = Invoke-RestMethod -Uri $apiUrl -Headers $headers -Method Get

        return $response.value
    }
    catch {
        Write-Error "Erro ao buscar reposit√≥rios: $_"
        exit 1
    }
}

function Copy-Repository {
    param(
        [string]$RepoName,
        [string]$RepoUrl
    )

    $targetDir = Join-Path $ClonePath $RepoName
    $hadStash = $false

    # Construir URL com autentica√ß√£o
    $authUrl = $RepoUrl -replace "https://", "https://${Username}:${Pat}@"

    if (Test-Path (Join-Path $targetDir ".git")) {
        Write-Update "Atualizando: $RepoName"

        try {
            Push-Location $targetDir

            # Verificar se h√° mudan√ßas locais
            $diffOutput = git diff --quiet 2>&1
            $diffCachedOutput = git diff --cached --quiet 2>&1
            $hasChanges = ($LASTEXITCODE -ne 0)

            if ($hasChanges) {
                Write-Warning "  Mudan√ßas locais detectadas, fazendo stash..."
                $stashResult = git stash push -m "auto-stash antes de pull $(Get-Date -Format 'yyyyMMdd-HHmmss')" --quiet 2>&1
                if ($LASTEXITCODE -eq 0) {
                    $hadStash = $true
                    $script:ReposStashed += $RepoName
                }
                else {
                    Write-Error "  Falha ao fazer stash"
                    $script:ReposFailed += "$RepoName (stash falhou)"
                    Pop-Location
                    return $false
                }
            }

            # Fazer pull
            git pull --quiet 2>&1 | Out-Null
            if ($LASTEXITCODE -eq 0) {
                $script:ReposUpdated += $RepoName
                if ($hadStash) {
                    Write-Info "  Restaurando stash..."
                    git stash pop --quiet 2>&1 | Out-Null
                    if ($LASTEXITCODE -ne 0) {
                        Write-Warning "  ‚ö†Ô∏è Conflito ao restaurar stash. Use 'git stash pop' manualmente."
                    }
                }
                Write-Success "OK: $RepoName"
            }
            else {
                Write-Error "  Falha no pull"
                $script:ReposFailed += "$RepoName (pull falhou)"
                if ($hadStash) {
                    git stash pop --quiet 2>&1 | Out-Null
                }
                Pop-Location
                return $false
            }

            Pop-Location
        }
        catch {
            Write-Warning "Falha ao atualizar $RepoName"
            $script:ReposFailed += "$RepoName (erro: $_)"
            Pop-Location
            return $false
        }
    }
    else {
        Write-Install "Clonando: $RepoName"
        try {
            git clone --quiet $authUrl $targetDir 2>&1 | Out-Null
            if ($LASTEXITCODE -eq 0) {
                $script:ReposCloned += $RepoName
                Write-Success "OK: $RepoName"
            }
            else {
                Write-Error "Falha ao clonar: $RepoName"
                $script:ReposFailed += "$RepoName (clone falhou)"
                return $false
            }
        }
        catch {
            Write-Warning "Falha ao clonar $RepoName"
            $script:ReposFailed += "$RepoName (clone falhou)"
            return $false
        }
    }

    return $true
}

function Write-Summary {
    Write-Host ""
    Write-Host "============================================================"
    Write-Step "RESUMO DA EXECU√á√ÉO"
    Write-Host "============================================================"
    Write-Host ""

    # Clonados
    if ($script:ReposCloned.Count -gt 0) {
        Write-Install "Reposit√≥rios clonados ($($script:ReposCloned.Count)):"
        foreach ($repo in $script:ReposCloned) {
            Write-Host "    ‚Ä¢ $repo"
        }
        Write-Host ""
    }

    # Atualizados
    if ($script:ReposUpdated.Count -gt 0) {
        Write-Update "Reposit√≥rios atualizados ($($script:ReposUpdated.Count)):"
        foreach ($repo in $script:ReposUpdated) {
            Write-Host "    ‚Ä¢ $repo"
        }
        Write-Host ""
    }

    # Com stash
    if ($script:ReposStashed.Count -gt 0) {
        Write-Warning "Reposit√≥rios com stash aplicado ($($script:ReposStashed.Count)):"
        foreach ($repo in $script:ReposStashed) {
            Write-Host "    ‚Ä¢ $repo"
        }
        Write-Host ""
    }

    # Falhas
    if ($script:ReposFailed.Count -gt 0) {
        Write-Error "Reposit√≥rios com falha ($($script:ReposFailed.Count)):"
        foreach ($repo in $script:ReposFailed) {
            Write-Host "    ‚Ä¢ $repo"
        }
        Write-Host ""
    }

    # Totalizador
    Write-Host "------------------------------------------------------------"
    $total = $script:ReposCloned.Count + $script:ReposUpdated.Count + $script:ReposFailed.Count
    $success = $script:ReposCloned.Count + $script:ReposUpdated.Count
    Write-Host "üìä TOTAL: $success de $total reposit√≥rios processados com sucesso" -ForegroundColor White
    Write-Host "   üì¶ Clonados:    $($script:ReposCloned.Count)"
    Write-Host "   üîÑ Atualizados: $($script:ReposUpdated.Count)"
    Write-Host "   üìÇ Com stash:   $($script:ReposStashed.Count)"
    Write-Host "   ‚ùå Falhas:      $($script:ReposFailed.Count)"
    Write-Host "------------------------------------------------------------"
    Write-Info "Local: $ClonePath"
}

# ============================================================================
# Main
# ============================================================================

function Main {
    Write-Step "Clone de Reposit√≥rios Azure DevOps"
    Write-Host ""

    # Tentar carregar config se par√¢metros n√£o foram fornecidos
    if ([string]::IsNullOrEmpty($OrganizationUrl)) {
        if (-not [string]::IsNullOrEmpty($ConfigFile)) {
            Import-ConfigFile -Path $ConfigFile | Out-Null
        }
        elseif (Test-Path $DefaultConfigFile) {
            Import-ConfigFile -Path $DefaultConfigFile | Out-Null
        }
    }

    Test-Prerequisites
    Test-Configuration

    # Criar pasta de destino
    if (-not (Test-Path $ClonePath)) {
        Write-Info "Criando pasta: $ClonePath"
        New-Item -ItemType Directory -Path $ClonePath -Force | Out-Null
    }

    # Buscar reposit√≥rios
    $repos = Get-Repositories

    if ($repos.Count -eq 0) {
        Write-Warning "Nenhum reposit√≥rio encontrado no projeto"
        exit 0
    }

    foreach ($repo in $repos) {
        Copy-Repository -RepoName $repo.name -RepoUrl $repo.remoteUrl | Out-Null
    }

    Write-Summary
}

Main

# ============================================================================
# Script: export-git-repos.ps1
# Description: Procura repos Git em uma pasta, identifica remote/branch e gera
#              script para clonar a estrutura em outro computador
# ============================================================================

param(
    [string]$Path,
    [string]$Output
)

# ============================================================================
# Helper Functions
# ============================================================================

function Write-Step {
    param([string]$Message)
    Write-Host "üöÄ $Message" -ForegroundColor White
}

function Write-Info {
    param([string]$Message)
    Write-Host "üîç $Message" -ForegroundColor Cyan
}

function Write-Success {
    param([string]$Message)
    Write-Host "‚úÖ $Message" -ForegroundColor Green
}

function Write-Warn {
    param([string]$Message)
    Write-Host "‚ö†Ô∏è $Message" -ForegroundColor Yellow
}

function Write-Err {
    param([string]$Message)
    Write-Host "‚ùå $Message" -ForegroundColor Red
}

# Script directory
$ScriptDir = Split-Path -Parent $MyInvocation.MyCommand.Path

# ============================================================================
# Functions
# ============================================================================

function Show-Help {
    @"
Uso: .\export-git-repos.ps1 [OP√á√ïES]

Procura reposit√≥rios Git em uma pasta e gera script para clonar a estrutura.

OP√á√ïES:
    -Path PATH       Pasta raiz para buscar repos (padr√£o: diret√≥rio atual)
    -Output FILE     Arquivo de sa√≠da para o script (padr√£o: clone-repos.ps1)

EXEMPLOS:
    .\export-git-repos.ps1 -Path C:\Projetos
    .\export-git-repos.ps1 -Path D:\Repos -Output meus-repos.ps1

"@
}

function Get-SearchPath {
    param([string]$InputPath)

    if ([string]::IsNullOrWhiteSpace($InputPath)) {
        $InputPath = Read-Host "üìÅ Pasta raiz para buscar repos [$(Get-Location)]"
        if ([string]::IsNullOrWhiteSpace($InputPath)) {
            $InputPath = (Get-Location).Path
        }
    }

    # Resolver path absoluto
    $resolvedPath = Resolve-Path -Path $InputPath -ErrorAction SilentlyContinue

    if (-not $resolvedPath -or -not (Test-Path $resolvedPath)) {
        Write-Err "Pasta n√£o encontrada: $InputPath"
        exit 1
    }

    return $resolvedPath.Path
}

function Get-OutputFile {
    param([string]$InputFile)

    if ([string]::IsNullOrWhiteSpace($InputFile)) {
        $InputFile = Read-Host "üìÑ Nome do arquivo de sa√≠da [clone-repos.ps1]"
        if ([string]::IsNullOrWhiteSpace($InputFile)) {
            $InputFile = "clone-repos.ps1"
        }
    }

    # Garantir extens√£o .ps1
    if (-not $InputFile.EndsWith(".ps1")) {
        $InputFile = "$InputFile.ps1"
    }

    return $InputFile
}

function Find-GitRepos {
    param([string]$SearchPath)

    Write-Info "Buscando reposit√≥rios Git em: $SearchPath"

    $gitFolders = Get-ChildItem -Path $SearchPath -Directory -Recurse -Filter ".git" -ErrorAction SilentlyContinue -Force

    $repos = @()
    foreach ($gitFolder in $gitFolders) {
        $repos += $gitFolder.Parent.FullName
    }

    return $repos
}

function Get-RepoInfo {
    param([string]$RepoPath)

    Push-Location $RepoPath

    try {
        $remoteUrl = git remote get-url origin 2>$null
        $currentBranch = git branch --show-current 2>$null

        if ([string]::IsNullOrWhiteSpace($currentBranch)) {
            $currentBranch = "main"
        }
    }
    catch {
        $remoteUrl = ""
        $currentBranch = "main"
    }

    Pop-Location

    return @{
        RemoteUrl = $remoteUrl
        Branch = $currentBranch
    }
}

function New-CloneScript {
    param(
        [string]$SearchPath,
        [string]$OutputFile
    )

    Write-Step "Gerando script de clonagem..."

    $repoCount = 0

    # Cabe√ßalho do script
    $scriptContent = @'
# ============================================================================
# Script: clone-repos.ps1 (gerado automaticamente)
# Description: Clona reposit√≥rios Git mantendo estrutura de pastas original
# ============================================================================

param(
    [string]$BaseDir = (Get-Location).Path
)

# ============================================================================
# Helper Functions
# ============================================================================

function Write-Step {
    param([string]$Message)
    Write-Host "üöÄ $Message" -ForegroundColor White
}

function Write-Info {
    param([string]$Message)
    Write-Host "üîç $Message" -ForegroundColor Cyan
}

function Write-Success {
    param([string]$Message)
    Write-Host "‚úÖ $Message" -ForegroundColor Green
}

function Write-Warn {
    param([string]$Message)
    Write-Host "‚ö†Ô∏è $Message" -ForegroundColor Yellow
}

function Write-Err {
    param([string]$Message)
    Write-Host "‚ùå $Message" -ForegroundColor Red
}

# ============================================================================
# Clone Function
# ============================================================================

function Invoke-CloneRepo {
    param(
        [string]$RelativePath,
        [string]$RemoteUrl,
        [string]$Branch
    )

    $targetDir = Join-Path $BaseDir $RelativePath

    if (Test-Path (Join-Path $targetDir ".git")) {
        Write-Warn "Repo j√° existe: $RelativePath"
        return
    }

    Write-Info "Clonando: $RelativePath"
    Write-Info "  URL: $RemoteUrl"
    Write-Info "  Branch: $Branch"

    $parentDir = Split-Path -Parent $targetDir
    if (-not (Test-Path $parentDir)) {
        New-Item -Path $parentDir -ItemType Directory -Force | Out-Null
    }

    try {
        git clone --branch $Branch $RemoteUrl $targetDir 2>$null
        if ($LASTEXITCODE -eq 0) {
            Write-Success "Clonado: $RelativePath"
        } else {
            throw "Clone failed"
        }
    }
    catch {
        # Tentar sem branch espec√≠fica
        try {
            git clone $RemoteUrl $targetDir 2>$null
            Write-Warn "Clonado (branch padr√£o): $RelativePath"
        }
        catch {
            Write-Err "Falha ao clonar: $RelativePath"
        }
    }
}

Write-Step "Clonando reposit√≥rios para: $BaseDir"

# ============================================================================
# Repositories
# ============================================================================

'@

    # Adicionar metadados
    $scriptContent += "`n# Gerado em: $(Get-Date -Format 'yyyy-MM-dd HH:mm:ss')`n"
    $scriptContent += "# Pasta original: $SearchPath`n`n"

    # Processar cada reposit√≥rio
    $repos = Find-GitRepos -SearchPath $SearchPath

    foreach ($repoPath in $repos) {
        $repoInfo = Get-RepoInfo -RepoPath $repoPath

        # Caminho relativo √† pasta de busca
        $relativePath = $repoPath.Substring($SearchPath.Length).TrimStart('\', '/')

        if ([string]::IsNullOrWhiteSpace($repoInfo.RemoteUrl)) {
            Write-Warn "Repo sem remote origin: $relativePath"
            $scriptContent += "# AVISO: Repo local sem remote - $relativePath`n"
            continue
        }

        $scriptContent += "Invoke-CloneRepo -RelativePath `"$relativePath`" -RemoteUrl `"$($repoInfo.RemoteUrl)`" -Branch `"$($repoInfo.Branch)`"`n"
        $repoCount++

        Write-Info "Encontrado: $relativePath"
        Write-Host "           Branch: $($repoInfo.Branch)"
    }

    # Footer do script
    $scriptContent += @'

# ============================================================================
# Summary
# ============================================================================

Write-Success "Processo de clonagem conclu√≠do!"
'@

    # Salvar arquivo
    $scriptContent | Out-File -FilePath $OutputFile -Encoding utf8

    return $repoCount
}

# ============================================================================
# Main
# ============================================================================

function Main {
    Write-Step "Exportador de Reposit√≥rios Git"
    Write-Host ""

    $searchPath = Get-SearchPath -InputPath $Path
    $outputFile = Get-OutputFile -InputFile $Output

    Write-Host ""

    $count = New-CloneScript -SearchPath $searchPath -OutputFile $outputFile

    Write-Host ""
    Write-Success "Script gerado: $outputFile"
    Write-Success "Total de reposit√≥rios: $count"
    Write-Host ""
    Write-Info "Para usar em outro computador:"
    Write-Host "    1. Copie o arquivo '$outputFile' para o destino"
    Write-Host "    2. Execute: .\$outputFile -BaseDir [pasta_destino]"
}

Main

