# Start MCP Memory Dashboard
# Inicia o servidor HTTP do mcp-memory-service com o dashboard web.
# Acesso: http://localhost:8000
#
# Uso: .\start-memory-dashboard.ps1 [-Port 8000] [-NoBrowser]

param(
    [int]$Port = 8000,
    [switch]$NoBrowser
)

# --- Carregar credenciais do credentials.yml ---
$configDir = $PSScriptRoot
$credFile = Join-Path $configDir "credentials.yml"

if (!(Test-Path $credFile)) {
    Write-Host " credentials.yml no encontrado em $configDir" -ForegroundColor Red
    exit 1
}

# Parse simples do YAML (sem dependncia externa)
$credContent = Get-Content $credFile -Raw
$accountId = if ($credContent -match 'account_id:\s*"([^"]+)"') { $Matches[1] }
$apiToken = if ($credContent -match 'api_token:\s*"([^"]+)"') { $Matches[1] }
$vectorizeIndex = if ($credContent -match 'vectorize_index:\s*"([^"]+)"') { $Matches[1] }
$d1DatabaseId = if ($credContent -match 'd1_database_id:\s*"([^"]+)"') { $Matches[1] }
$storageBackend = if ($credContent -match 'storage_backend:\s*"([^"]+)"') { $Matches[1] } else { "cloudflare" }

if (!$accountId -or !$apiToken) {
    Write-Host " Credenciais Cloudflare incompletas no credentials.yml" -ForegroundColor Red
    exit 1
}

# --- Detectar Python ---
$pythonPath = $null

# 1. Tentar o caminho do mcp.json do workspace (gerado por mquina, nunca hardcodar)
# Navegar: config/ -> .copilot-core/ -> workspace/ -> .vscode/mcp.json
$mcpJson = Join-Path $configDir "..\..\..\.vscode\mcp.json"
if (Test-Path $mcpJson) {
    $mcpContent2 = Get-Content $mcpJson -Raw | ConvertFrom-Json
    $candidate = $mcpContent2.servers.memory.command
    if ($candidate -and (Test-Path $candidate)) {
        $pythonPath = $candidate
    }
}

# 2. Fallback: python3/python no PATH (skip WindowsApps stubs)
if (!$pythonPath) {
    $candidates = @("python3", "python")
    foreach ($cmd in $candidates) {
        $found = Get-Command $cmd -ErrorAction SilentlyContinue | Select-Object -ExpandProperty Source
        if ($found -and $found -notmatch "WindowsApps") {
            $pythonPath = $found
            break
        }
    }
}

if (!$pythonPath) {
    Write-Host " Python no encontrado. Instale Python 3.12+" -ForegroundColor Red
    exit 1
}

Write-Host " MCP Memory Dashboard" -ForegroundColor Cyan
Write-Host "   Python:  $pythonPath" -ForegroundColor DarkGray
Write-Host "   Backend: $storageBackend" -ForegroundColor DarkGray
Write-Host "   URL:     http://localhost:$Port" -ForegroundColor DarkGray
Write-Host ""

# --- Configurar variveis de ambiente ---
$env:MCP_MEMORY_STORAGE_BACKEND = $storageBackend
$env:CLOUDFLARE_ACCOUNT_ID = $accountId
$env:CLOUDFLARE_API_TOKEN = $apiToken
$env:CLOUDFLARE_VECTORIZE_INDEX = $vectorizeIndex
$env:CLOUDFLARE_D1_DATABASE_ID = $d1DatabaseId
$env:MCP_HTTP_PORT = $Port
$env:MCP_ALLOW_ANONYMOUS_ACCESS = "true"

# --- Abrir browser automaticamente ---
if (!$NoBrowser) {
    Start-Job -ScriptBlock {
        param($url)
        Start-Sleep -Seconds 5
        Start-Process $url
    } -ArgumentList "http://localhost:$Port" | Out-Null
    Write-Host " Browser abrir automaticamente em 5s..." -ForegroundColor Yellow
}

Write-Host " Iniciando servidor... (Ctrl+C para parar)" -ForegroundColor Green
Write-Host ""

# --- Iniciar servidor HTTP ---
$memoryExe = Join-Path (Split-Path $pythonPath) "Scripts\memory.exe"

if (Test-Path $memoryExe) {
    & $memoryExe server --http --storage-backend $storageBackend
} else {
    & $pythonPath -m mcp_memory_service.cli server --http --storage-backend $storageBackend
}

"""Verify specific skill behavioral contracts (documented expectations)."""

import pytest


@pytest.mark.behavioral
class TestSkillContracts:
    def test_turn_reflect_has_all_phases(self, all_skills):
        """agent-turn-reflect must document Fase 0 through Fase 4."""
        skill = next(
            (s for s in all_skills if s.name == "agent-turn-reflect"), None
        )
        if skill is None:
            pytest.fail("agent-turn-reflect skill not found")

        for phase in range(5):
            assert f"Fase {phase}" in skill.content, (
                f"agent-turn-reflect: missing Fase {phase}"
            )

    def test_turn_reflect_has_triage(self, all_skills):
        """agent-turn-reflect must document triage rapido."""
        skill = next(
            (s for s in all_skills if s.name == "agent-turn-reflect"), None
        )
        if skill is None:
            pytest.fail("agent-turn-reflect skill not found")

        assert "Triage" in skill.content or "triage" in skill.content

    def test_memory_register_has_categories(self, all_skills):
        """agent-memory-register must document standard categories."""
        skill = next(
            (s for s in all_skills if s.name == "agent-memory-register"),
            None,
        )
        if skill is None:
            pytest.skip("agent-memory-register not found")

        expected = ["arquitetura", "padres", "tooling", "workflow", "debug"]
        for cat in expected:
            assert cat in skill.content, (
                f"agent-memory-register: missing category '{cat}'"
            )

    def test_chat_save_has_incremental_mode(self, all_skills):
        """agent-chat-save must have incremental mode for per-turn appends."""
        skill = next(
            (s for s in all_skills if s.name == "agent-chat-save"), None
        )
        if skill is None:
            pytest.skip("agent-chat-save not found")

        assert "Modo Incremental" in skill.content, (
            "agent-chat-save: missing 'Modo Incremental' section"
        )

    def test_action_review_has_side_effect_checks(self, all_skills):
        """agent-action-review must check codemap, memory, and context."""
        skill = next(
            (s for s in all_skills if s.name == "agent-action-review"), None
        )
        if skill is None:
            pytest.skip("agent-action-review not found")

        for item in ["Codemap", "Memria", "Contexto"]:
            assert item in skill.content, (
                f"agent-action-review: missing side-effect check '{item}'"
            )

    def test_git_guard_blocks_by_default(self, all_skills):
        """agent-git-guard must default to blocking git operations."""
        skill = next(
            (s for s in all_skills if s.name == "agent-git-guard"), None
        )
        if skill is None:
            pytest.skip("agent-git-guard not found")

        has_default_block = (
            "sem git" in skill.content.lower()
            or "no executar" in skill.content.lower()
            or "block" in skill.content.lower()
            or "sem config" in skill.content.lower()
        )
        assert has_default_block, (
            "agent-git-guard: should default to blocking git operations"
        )

