    {
        if (string.IsNullOrWhiteSpace(value))
            throw new ArgumentException(
                "Message content cannot be empty", 
                nameof(value));
                
        if (value.Length > MaxLength)
            throw new ArgumentException(
                $"Message content cannot exceed {MaxLength} characters", 
                nameof(value));
        
        Value = value.Trim();
    }
    
    public bool ContainsUrl()
    {
        return System.Text.RegularExpressions.Regex.IsMatch(
            Value, 
            @"https?://\S+");
    }
    
    public MessageContentVO Truncate(int maxLength)
    {
        if (Value.Length <= maxLength)
            return this;
            
        return new MessageContentVO(
            Value[..(maxLength - 3)] + "...");
    }
    
    public static implicit operator string(MessageContentVO content) 
        => content.Value;
    
    public override string ToString() => Value;
}
```

### Value Object Enumerable

```csharp
namespace BeWiser.Assistant.Domain.ValueObjects;

/// <summary>
/// Represents a collection of tags.
/// </summary>
public sealed record TagsVO
{
    private readonly HashSet<string> _tags;
    
    public IReadOnlySet<string> Values => _tags;
    public int Count => _tags.Count;
    
    public TagsVO(IEnumerable<string> tags)
    {
        _tags = tags
            .Where(t => !string.IsNullOrWhiteSpace(t))
            .Select(t => t.Trim().ToLowerInvariant())
            .ToHashSet();
    }
    
    public TagsVO() : this(Array.Empty<string>()) { }
    
    public TagsVO Add(string tag)
    {
        var newTags = new HashSet<string>(_tags) { tag.Trim().ToLowerInvariant() };
        return new TagsVO(newTags);
    }
    
    public TagsVO Remove(string tag)
    {
        var newTags = new HashSet<string>(_tags);
        newTags.Remove(tag.Trim().ToLowerInvariant());
        return new TagsVO(newTags);
    }
    
    public bool Contains(string tag) 
        => _tags.Contains(tag.Trim().ToLowerInvariant());
}
```

## Regras de Value Objects

1. **Imutabilidade**: Usar `record` ou propriedades `init`
2. **Valida√ß√£o no construtor**: Garantir estado v√°lido
3. **Sem identidade**: Igualdade por valor, n√£o por refer√™ncia
4. **Auto-valida√ß√£o**: O VO √© respons√°vel por sua pr√≥pria validade
5. **Convers√£o impl√≠cita**: Para tipo primitivo quando faz sentido

