        // Assert
        act.Should().Throw<ArgumentException>();
    }

    [Fact]
    public void ImplicitConversion_ToString_ReturnsValue()
    {
        // Arrange
        var phoneNumber = new PhoneNumberVO("+5511999999999");

        // Act
        string result = phoneNumber;

        // Assert
        result.Should().Be("+5511999999999");
    }
