commandLine.original:
$gitDir = "D:\Repos\_pm\copilot\.copilot-project\.git"

$worktreeRefs = @{
  "docker-framework" = @{ ref = "refs/remotes/origin/_pm/docker-framework"; sha = "ed3a911dccfb0c07683f53b66039630411230d2e" }
  "everyday_queries" = @{ ref = "refs/remotes/origin/_pm/everyday_queries"; sha = "07af5628bd7ff0067d29cd104a6bcba254b225cc" }
  "bewiser.assistant.api" = @{ ref = "refs/remotes/origin/_bw/bewiser.assistant.api"; sha = "7cc15ac7c5c299c86b4b842a0aec977f6ffabdd3" }
  "STT" = @{ ref = "refs/remotes/origin/_hv/STT"; sha = "04a8c92950fdc943054300c66f46f11dfc21f3dd" }
}

foreach ($wt in $worktreeRefs.GetEnumerator()) {
  $refPath = Join-Path $gitDir "worktrees" $wt.Key $wt.Value.ref
  $dir = Split-Path $refPath -Parent
  if (-not (Test-Path $dir)) {
    New-Item -ItemType Directory -Path $dir -Force | Out-Null
    Write-Output "Created dir: $dir"
  }
  Set-Content -Path $refPath -Value $wt.Value.sha -NoNewline
  Write-Output "Created ref: $refPath"
}
Write-Output "Done"

