{"content": "# Script to install custom Oh My Posh theme
# Author: GitHub Copilot
# Date: 2026-01-15

Write-Host "üé® Installing custom Oh My Posh theme..." -ForegroundColor Cyan

# Check if Oh My Posh is installed
Write-Host "üîç Checking Oh My Posh installation..." -ForegroundColor Yellow
$ohMyPoshInstalled = Get-Command oh-my-posh -ErrorAction SilentlyContinue

if (-not $ohMyPoshInstalled) {
    Write-Host "‚ùå Oh My Posh is not installed!" -ForegroundColor Red
    Write-Host "üì¶ Installing Oh My Posh via winget..." -ForegroundColor Yellow

    try {
        winget install JanDeDobbeleer.OhMyPosh -s winget --accept-package-agreements --accept-source-agreements

        # Update PATH for current session
        $env:Path = [System.Environment]::GetEnvironmentVariable("Path","Machine") + ";" + [System.Environment]::GetEnvironmentVariable("Path","User")

        Write-Host "‚úÖ Oh My Posh installed successfully!" -ForegroundColor Green
        Write-Host "üí° You may need to restart the terminal to use Oh My Posh" -ForegroundColor Cyan
    } catch {
        Write-Host "‚ùå Error installing Oh My Posh: $_" -ForegroundColor Red
        Write-Host "üí° Try installing manually: winget install JanDeDobbeleer.OhMyPosh" -ForegroundColor Yellow
        exit 1
    }
} else {
    Write-Host "‚úÖ Oh My Posh is already installed" -ForegroundColor Green
    Write-Host "üîÑ Updating Oh My Posh..." -ForegroundColor Yellow

    try {
        oh-my-posh upgrade --force
        Write-Host "‚úÖ Oh My Posh updated successfully!" -ForegroundColor Green
    } catch {
        Write-Host "‚ö†Ô∏è  Could not update, but continuing with current version" -ForegroundColor Yellow
    }
}

# Theme URL
$themeUrl = "https://raw.githubusercontent.com/pdmartins/scripts/refs/heads/main/oh-my-posh/blocks.emoji.omp.json"

# Oh My Posh themes directory
$themesPath = "$env:POSH_THEMES_PATH"
if (-not $themesPath) {
    $themesPath = "$env:LOCALAPPDATA\Programs\oh-my-posh\themes"
}

# Theme file name
$themeName = "blocks.emoji.omp.json"
$themeFilePath = Join-Path $themesPath $themeName

Write-Host "üìÅ Themes directory: $themesPath" -ForegroundColor Yellow

# Create directory if it doesn't exist
if (-not (Test-Path $themesPath)) {
    Write-Host "üìÇ Creating themes directory..." -ForegroundColor Yellow
    New-Item -ItemType Directory -Path $themesPath -Force | Out-Null
}

# Download theme
try {
    Write-Host "‚¨áÔ∏è  Downloading theme from Gist..." -ForegroundColor Yellow
    Invoke-WebRequest -Uri $themeUrl -OutFile $themeFilePath -UseBasicParsing
    Write-Host "‚úÖ Theme downloaded successfully: $themeFilePath" -ForegroundColor Green
} catch {
    Write-Host "‚ùå Error downloading theme: $_" -ForegroundColor Red
    exit 1
}

# Check if profile exists
if (-not (Test-Path $PROFILE)) {
    Write-Host "üìù Creating profile file..." -ForegroundColor Yellow
    New-Item -Path $PROFILE -Type File -Force | Out-Null
    $profileContent = ""
} else {
    # Read current profile content
    $profileContent = Get-Content $PROFILE -Raw -ErrorAction SilentlyContinue
    if (-not $profileContent) {
        $profileContent = ""
    }
}

# Check if oh-my-posh init line already exists in profile
$ohMyPoshPattern = "oh-my-posh\s+init"
$newInitLine = "oh-my-posh init pwsh --config '$themeFilePath' | Invoke-Expression"

if ($profileContent -match $ohMyPoshPattern) {
    Write-Host "üîÑ Updating existing Oh My Posh configuration in profile..." -ForegroundColor Yellow

    # Replace any line containing "oh-my-posh init"
    $profileContent = $profileContent -replace ".*oh-my-posh\s+init[^\r\n]*", $newInitLine

    Set-Content $PROFILE -Value $profileContent -Encoding UTF8
    Write-Host "‚úÖ Oh My Posh configuration updated in profile" -ForegroundColor Green
} else {
    Write-Host "‚ûï Adding Oh My Posh to profile..." -ForegroundColor Yellow

    # Add at the beginning of profile if not empty, otherwise create
    if ($profileContent.Trim()) {
        $profileContent = $newInitLine + "`r`n`r`n" + $profileContent
    } else {
        $profileContent = $newInitLine + "`r`n"
    }

    Set-Content $PROFILE -Value $profileContent -Encoding UTF8
    Write-Host "‚úÖ Oh My Posh added to profile" -ForegroundColor Green
}

Write-Host "`n‚ú® Installation complete!" -ForegroundColor Green
Write-Host "üìã To apply changes, run:" -ForegroundColor Cyan
Write-Host "   . `$PROFILE" -ForegroundColor White
Write-Host "`nüí° Or close and reopen PowerShell" -ForegroundColor Cyan
