using Google.Cloud.BigQuery.V2;
using Hapvida.TI.VoiceTranscription.Domain.Contracts.v1.Services;
using Hapvida.TI.VoiceTranscription.Domain.Contracts.v1.Wrappers;
using Hapvida.TI.VoiceTranscription.Domain.Dtos;
using Hapvida.TI.VoiceTranscription.Domain.Models.v1;
using Microsoft.Extensions.Logging;
using Microsoft.Extensions.Options;

namespace Hapvida.TI.VoiceTranscription.Domain.Services.v1
{
    public class BigQueryCheckStatusService : IBigQueryServiceReader
    {
        private readonly GoogleCloudStorageSettings _googleCloudSettings;
        private readonly ILogger<BigQueryCheckStatusService> _logger;
        private readonly IBigQueryClientWrapper _bigQueryClientWrapper;

        public BigQueryCheckStatusService(
            IOptions<GoogleCloudStorageSettings> gcsOptions,
            ILogger<BigQueryCheckStatusService> logger,
            IBigQueryClientWrapper bigQueryClientWrapper)
        {
            _googleCloudSettings = gcsOptions.Value;
            if (gcsOptions is null) throw new ArgumentNullException(nameof(gcsOptions));

            _logger = logger ?? throw new ArgumentNullException(nameof(logger));
            _bigQueryClientWrapper = bigQueryClientWrapper ?? throw new ArgumentNullException(nameof(bigQueryClientWrapper));

            ValidateConfiguration();
        }

        public async Task<TranscriptionSummaryDto?> GetLatestTranscriptionByWorkIdAsync(string workId, CancellationToken ct)
        {
            if (ct.IsCancellationRequested)
            {
                _logger.LogWarning(
                    "Opera√ß√£o cancelada antes da consulta de resumo ao BigQuery para WorkId={WorkId}",
                    workId);

                ct.ThrowIfCancellationRequested();
            }

            if (string.IsNullOrWhiteSpace(workId))
            {
                _logger.LogWarning(
                    "WorkId inv√°lido ao consultar resumo no BigQuery em {Service}",
                    nameof(BigQueryCheckStatusService));

                return null;
            }

            try
            {
                var bq = _googleCloudSettings.GoogleBigQuery
                    ?? throw new InvalidOperationException("Configura√ß√£o de GoogleBigQuery n√£o foi informada.");

                var tableFq = $"`{_googleCloudSettings.ProjectId}.{bq.DataSet}.{bq.Table}`";

                const string sqlTemplate = @"
                SELECT
                  workId,
                  createdAt
                FROM {0}
                WHERE workId = @workId
                ORDER BY createdAt DESC
                LIMIT 1";

                var sql = string.Format(sqlTemplate, tableFq);

                _logger.LogInformation(
                    "Iniciando consulta de resumo ao BigQuery para WorkId={WorkId} na tabela {Table}",
                    workId,
                    tableFq);

                var parameters = new[]
                {
                    new BigQueryParameter("workId", BigQueryDbType.String, workId)
                };

                var results = await _bigQueryClientWrapper.ExecuteQueryAsync(
                    sql,
                    parameters,
                    null,
                    cancellationToken: ct);

                var row = results.FirstOrDefault();

                if (row is null)
                {
                    _logger.LogInformation(
                        "Nenhum resumo encontrado no BigQuery para WorkId={WorkId}",
                        workId);

                    return null;
                }

                var dto = MapToTranscriptionSummaryDto(row, workId);

                _logger.LogInformation(
                    "Resumo de transcri√ß√£o recuperado do BigQuery para WorkId={WorkId} com CreatedAt={CreatedAt}",
                    workId,
                    dto.CreatedAt);

                return dto;
            }
            catch (OperationCanceledException)
            {
                _logger.LogWarning(
                    "Opera√ß√£o cancelada durante consulta de resumo ao BigQuery para WorkId={WorkId}",
                    workId);

                throw;
            }
            catch (Exception ex)
            {
                _logger.LogCritical(
                    ex,
                    "Erro inesperado em {Service} ao consultar resumo no BigQuery para WorkId={WorkId}",
                    nameof(BigQueryCheckStatusService),
                    workId);

                throw;
            }
        }

        public async Task<IReadOnlyList<TranscriptionSummaryDto>> GetTranscriptionsByPeriodAsync(DateTimeOffset start, DateTimeOffset end, CancellationToken ct)
        {
            if (ct.IsCancellationRequested)
            {
                _logger.LogWarning(
                    "Opera√ß√£o cancelada antes da consulta de transcri√ß√µes por per√≠odo ({Start} - {End}).",
                    start, end);

                ct.ThrowIfCancellationRequested();
            }

            try
            {
                var bq = _googleCloudSettings.GoogleBigQuery
                    ?? throw new InvalidOperationException("Configura√ß√£o de GoogleBigQuery n√£o foi informada.");

                var tableFq = $"`{_googleCloudSettings.ProjectId}.{bq.DataSet}.{bq.Table}`";

                const string sqlTemplate = @"
                                            SELECT
                                              workId,
                                              createdAt
                                            FROM {0}
                                            WHERE createdAt >= @startDate
                                              AND createdAt <= @endDate
                                            ORDER BY createdAt DESC";

                var sql = string.Format(sqlTemplate, tableFq);

                _logger.LogInformation(
                    "Iniciando consulta ao BigQuery para lista de transcri√ß√µes por per√≠odo ({Start} - {End}) na tabela {Table}",
                    start, end, tableFq);

                var parameters = new[]
                {

                    new BigQueryParameter("startDate", BigQueryDbType.Timestamp, start.UtcDateTime),
                    new BigQueryParameter("endDate",   BigQueryDbType.Timestamp, end.UtcDateTime)
                };

                var rows = await _bigQueryClientWrapper.ExecuteQueryAsync(
                    sql,
                    parameters,
                    null,
                    cancellationToken: ct);

                var list = rows
                    .Select(row => MapToTranscriptionSummaryDto(row))
                    .ToList();

                _logger.LogInformation(
                    "Consulta de transcri√ß√µes por per√≠odo conclu√≠da. Registros retornados: {Count}",
                    list.Count);

                return list;
            }
            catch (OperationCanceledException)
            {
                _logger.LogWarning(
                    "Opera√ß√£o cancelada durante consulta de transcri√ß√µes por per√≠odo ({Start} - {End}).",
                    start, end);
                throw;
            }
            catch (Exception ex)
            {
                _logger.LogCritical(
                    ex,
                    "Erro inesperado em {Service} ao consultar transcri√ß√µes por per√≠odo ({Start} - {End})",
                    nameof(BigQueryCheckStatusService),
                    start,
                    end);
                throw;
            }
        }

        public async Task<ConsultaStatusDto?> GetLatestByWorkIdAsync(string workId, CancellationToken ct)
        {
            if (ct.IsCancellationRequested)
            {
                _logger.LogWarning("Opera√ß√£o cancelada antes da consulta ao BigQuery para WorkId={WorkId}", workId);

                ct.ThrowIfCancellationRequested();
            }

            if (string.IsNullOrWhiteSpace(workId))
            {
                _logger.LogWarning("WorkId inv√°lido ao consultar BigQuery em {Service}", nameof(BigQueryCheckStatusService));
                return null;
            }

            try
            {
                var bq = _googleCloudSettings.GoogleBigQuery!;
                var tableFq = $"`{_googleCloudSettings.ProjectId}.{bq.DataSet}.{bq.Table}`";

                const string sqlTemplate = @"
                                            SELECT
                                              workId,
                                              encounterId,
                                              symptons,
                                              transcription,
                                              suggestedCIDs,
                                              workStatus,
                                              readyFields,
                                              createdAt
                                            FROM {0}
                                            WHERE workId = @workId
                                            ORDER BY createdAt DESC
                                            LIMIT 1";

                var sql = string.Format(sqlTemplate, tableFq);

                _logger.LogInformation("Iniciando consulta ao BigQuery para WorkId={WorkId} na tabela {Table}", workId, tableFq);

                var parameters = new[]
                {
                    new BigQueryParameter("workId", BigQueryDbType.String, workId), 
                };

                var results = await _bigQueryClientWrapper.ExecuteQueryAsync(
                        sql,
                        parameters,
                        null,
                        cancellationToken: ct);

                var row = results.FirstOrDefault();

                if (row is null)
                {
                    _logger.LogInformation(
                        "Nenhum registro encontrado no BigQuery para WorkId={WorkId}",
                        workId);

                    return null;
                }

                var dto = MapToConsultaStatusDto(row, workId);

                _logger.LogInformation(
                    "Registro de status recuperado do BigQuery para WorkId={WorkId} com WorkStatus={Status}",
                    workId,
                    dto.WorkStatus);

                return dto;
            }
            catch (OperationCanceledException)
            {
                _logger.LogWarning(
                    "Opera√ß√£o cancelada durante consulta ao BigQuery para WorkId={WorkId}",
                    workId);

                throw;
            }
            catch (Exception ex)
            {
                _logger.LogCritical(
                    ex,
                    "Erro inesperado em {Service} ao consultar BigQuery para WorkId={WorkId}",
                    nameof(BigQueryCheckStatusService),
                    workId);

                throw;
            }
        }

        private void ValidateConfiguration()
        {
            if (string.IsNullOrWhiteSpace(_googleCloudSettings.ProjectId))
                throw new InvalidOperationException("GoogleCloudStorageSettings.ProjectId √© obrigat√≥rio.");

            if (_googleCloudSettings.GoogleBigQuery is null)
                throw new InvalidOperationException("GoogleCloudStorageSettings.GoogleBigQuery √© obrigat√≥rio.");

            if (string.IsNullOrWhiteSpace(_googleCloudSettings.GoogleBigQuery.DataSet))
                throw new InvalidOperationException("GoogleBigQuery.DataSet √© obrigat√≥rio.");

            if (string.IsNullOrWhiteSpace(_googleCloudSettings.GoogleBigQuery.Table))
                throw new InvalidOperationException("GoogleBigQuery.Table √© obrigat√≥rio.");
        }

        private static ConsultaStatusDto MapToConsultaStatusDto(RowDto row, string workId)
        {
            var dto = new ConsultaStatusDto
            {
                WorkId = row.WorkId,
                EncounterId = row.EncounterId,
                Transcription = row.Transcription,
                WorkStatus = row.WorkStatus,
                CreatedAt = row.CreatedAt,
                Symptoms = row.Symptoms, 
                SuggestedCIDs = row.SuggestedCIDs
            };

            return dto;
        }

        private static TranscriptionSummaryDto MapToTranscriptionSummaryDto(RowDto row, string? workId = null)
        {
            var dto = new TranscriptionSummaryDto
            {
                WorkId = workId ?? row.WorkId,
                CreatedAt = row.CreatedAt
            };

            return dto;
        }
    }
}
