using AutoMapper;
using Hapvida.Core.Domain.Contracts;
using Hapvida.Core.Domain.Fixed;
using Hapvida.Core.Domain.Handlers;
using Hapvida.Core.Domain.Models;
using Hapvida.TI.VoiceTranscription.Domain.Contracts.v1.Services;
using Hapvida.TI.VoiceTranscription.Domain.Models.v1;
using Hapvida.TI.VoiceTranscription.Domain.Resources.v1;
using Microsoft.Extensions.Logging;
using System.Text.Json;

namespace Hapvvida.TI.VoiceTranscription.Infra.Data.Queries.Queries.v1.GetTranscription
{
    public class GetTranscriptionHandler : QueryHandler<GetTranscriptionQuery>
    {
        private readonly ILogger<GetTranscriptionHandler> _logger;
        private readonly ICacheService _cacheService;
        private readonly IDomainContextNotifications _domainContextNotifications;
        private readonly IBigQueryServiceReader _bigQueryServiceReader;
        private readonly IMapper _mapper;

        private static readonly JsonSerializerOptions CacheSerializerOptions = new()
        {
            PropertyNameCaseInsensitive = true
        };

        public GetTranscriptionHandler(
            ILogger<GetTranscriptionHandler> logger,
            ICacheService cacheService,
            IMapper mapper,
            IBigQueryServiceReader bigQueryServiceReader,
            IDomainContextNotifications domainContextNotifications)
        {
            _logger = logger ?? throw new ArgumentNullException(nameof(logger));
            _cacheService = cacheService ?? throw new ArgumentNullException(nameof(cacheService));
            _mapper = mapper ?? throw new ArgumentNullException(nameof(mapper));
            _bigQueryServiceReader = bigQueryServiceReader ?? throw new ArgumentNullException(nameof(bigQueryServiceReader));
            _domainContextNotifications = domainContextNotifications ?? throw new ArgumentNullException(nameof(domainContextNotifications));
        }

        public override async Task<Response> Handle(GetTranscriptionQuery query, CancellationToken cancellationToken)
        {
            _logger.LogInformation(LogsTemplate.StartHandler, nameof(GetTranscriptionHandler));
            _logger.LogDebug(LogsTemplate.TraceHandler, nameof(GetTranscriptionHandler), nameof(Handle), Message.StartExecutingHandler);

            if (cancellationToken.IsCancellationRequested)
            {
                _logger.LogWarning("Execu√ß√£o cancelada antes de iniciar processamento em {Handler} para WorkId={WorkId}", nameof(GetTranscriptionHandler), query.WorkId);

                return Response.None;
            }

            if (string.IsNullOrWhiteSpace(query.WorkId))
            {
                _domainContextNotifications.Add(Message.WorkIdRequired, NotificationType.ValidationError);
                _logger.LogWarning(LogsTemplate.WarningHandler, nameof(GetTranscriptionHandler), Message.WorkIdRequired);

                return Response.None;
            }

            var cachedResponse = await GetValueOnCacheAsync(query.WorkId, cancellationToken);

            if (cachedResponse is not null)
            {
                _logger.LogInformation(LogsTemplate.EndHandler, nameof(GetTranscriptionHandler), string.Format(Message.GettingFromCache, query.WorkId));

                _logger.LogInformation("Resposta retornada do cache para WorkId={WorkId}", query.WorkId);

                return new Response { Content = cachedResponse };
            }

            _logger.LogInformation("Consultando BigQuery para obter a √∫ltima transcri√ß√£o do WorkId={WorkId}", query.WorkId);

            var latestStatus = await _bigQueryServiceReader.GetLatestByWorkIdAsync(query.WorkId, cancellationToken);

            if (latestStatus is null)
            {
                _domainContextNotifications.Add(string.Format(Message.Pending, query.WorkId), NotificationType.Success);
                _logger.LogWarning(LogsTemplate.WarningHandler, nameof(GetTranscriptionHandler), $"{Message.TranscriptionNotFound}, Id : {query.WorkId}");

                return Response.None;
            }

            var responseDto = _mapper.Map<GetTranscriptionQueryResponse>(latestStatus);

            _logger.LogInformation(LogsTemplate.InfoHandler, nameof(GetTranscriptionHandler), Message.TitleCacheInfo, string.Format(Message.AddingToCache, query.WorkId));

            var serialized = JsonSerializer.Serialize(responseDto, CacheSerializerOptions);

            await _cacheService.SetCacheAsync(query.WorkId, serialized, cancellationToken);

            _logger.LogInformation(LogsTemplate.EndHandler, nameof(GetTranscriptionHandler), string.Format(Message.GettingFromBigQuery, query.WorkId));

            return new Response { Content = responseDto };
        }

        private async Task<GetTranscriptionQueryResponse?> GetValueOnCacheAsync(string key, CancellationToken cancellationToken)
        {
            var cacheValue = await _cacheService.GetCacheAsync(key, cancellationToken);

            if (string.IsNullOrWhiteSpace(cacheValue))
                return null;

            try
            {
                var deserialized = JsonSerializer.Deserialize<GetTranscriptionQueryResponse>(cacheValue, CacheSerializerOptions);

                if (deserialized is not null)
                {
                    _logger.LogDebug("Transcri√ß√£o encontrada no cache para WorkId={WorkId}", key);
                }

                return deserialized;
            }
            catch (JsonException ex)
            {
                _logger.LogError(ex, "Falha ao desserializar conte√∫do do cache para WorkId={WorkId}. Conte√∫do bruto: {Raw}", key, cacheValue);
                return null;
            }
        }
    }
}
