using Hapvida.Core.Domain.Handlers;
using Hapvida.Core.Domain.Models;
using Hapvida.TI.VoiceTranscription.Domain.Contracts.v1.Services;
using Hapvida.TI.VoiceTranscription.Domain.Dtos;
using Hapvida.TI.VoiceTranscription.Domain.Resources.v1;
using Microsoft.Extensions.Logging;

namespace Hapvida.TI.VoiceTranscription.Infra.Data.Commands.v1.CreateSignedUrlUpload;

public class CreateSignedUrlUploadHandler : CommandHandler<CreateSignedUrlUploadCommand>
{
    private readonly ILogger<CreateSignedUrlUploadHandler> _logger;
    private readonly IGenerateUrlSignedService _generateSignedUrlService;
    private readonly IBigQueryServiceWriter _bigQueryServiceWriter;

    public CreateSignedUrlUploadHandler(
        ILogger<CreateSignedUrlUploadHandler> logger,
        IGenerateUrlSignedService generateSignedUrlService,
        IBigQueryServiceWriter bigQueryServiceWriter)
    {
        _logger = logger;
        _generateSignedUrlService = generateSignedUrlService;
        _bigQueryServiceWriter = bigQueryServiceWriter;
    }

    public override async Task<Response> Handle(CreateSignedUrlUploadCommand command, CancellationToken cancellationToken)
    {
        _logger.LogInformation(LogsTemplate.StartHandler, nameof(CreateSignedUrlUploadHandler));
        _logger.LogDebug(LogsTemplate.TraceHandler, nameof(CreateSignedUrlUploadHandler), nameof(Handle), Message.StartExecutingHandler);

        var (signedUrl, workId) = await _generateSignedUrlService.GenerateForUploadAsync(command.Prompt, command.Platform);

        var record = new SignedUrlRecordDto
        {
            WorkId = workId,
            PromptId = command.Prompt,
            Platform = command.Platform,
            SignedUrl = signedUrl,
            CreatedAt = DateTime.UtcNow
        };

        var inserted = await _bigQueryServiceWriter.InsertSignedUrlRecordAsync(record, cancellationToken);

        if (!inserted)
        {
            _logger.LogWarning("Falha ao registrar informa√ß√µes no BigQuery para WorkId={WorkId}", workId);
        }

        return new Response
        {
            Content = new CreateSignedUrlUploadResponse
            {
                SignedUrl = signedUrl,
                WorkId = workId,
                PromptId = command.Prompt,
                Platform = command.Platform,
            }
        };
    }
}
