        [Fact(DisplayName = "WorkId em branco ‚Üí notifica√ß√£o de valida√ß√£o e Response.None")]
        public async Task Handle_WhenWorkIdEmpty_ShouldNotifyValidation_AndReturnNone()
        {
            var sut = BuildSut(out var logger, out var cache, out var ctx, out var mapper, out var bq);
            var query = new GetTranscriptionQuery(" ");

