    public static IServiceCollection AddDistributedCache(this IServiceCollection services, IConfiguration configuration)
    {
        var cacheSettings = configuration.GetSection(CacheSettings.SessionName).Get<CacheSettings>();

        ArgumentNullException.ThrowIfNull(cacheSettings);

        services.AddSingleton(cacheSettings);
        services.AddSingleton<ICacheService, CacheService>();

        return services.AddStackExchangeRedisCache(op =>
        {
            op.Configuration = cacheSettings.ConnectionString;
            op.InstanceName = cacheSettings.InstanceName;
        });
    }
    public static IServiceCollection AddApplicationRepository(this IServiceCollection services, IConfiguration configuration)
    {
        services.Configure<GoogleCredentialSettings>(configuration.GetSection("Core:VoiceTranscription:Credential"));
        services.Configure<PubSubSettings>(configuration.GetSection("Core:VoiceTranscription:PubSub"));
        services.Configure<BigQuerySettings>(configuration.GetSection("Core:VoiceTranscription:BigQuery"));
        services.Configure<VertexSettings>(configuration.GetSection("Core:VoiceTranscription:Vertex"));

        services.AddSingleton<IBigQueryService, BigQueryService>();
        services.AddSingleton<IVertexAiService, VertexAiService>();
        services.AddSingleton<IGoogleCredentialProvider, GoogleCredentialProvider>();
        services.AddSingleton<ITranscriptionMessageProcessor, TranscriptionMessageProcessor>();
        services.AddSingleton<IVertexClientWrapper, VertexClientWrapper>();

        return services;
    }
}
