```csharp
using Google.Apis.Auth.OAuth2;
using Google.Apis.Bigquery.v2;
using Google.GenAI.Types;
using Hapvida.TI.VoiceTranscription.Ai.Domain.Contracts.v1;
using Hapvida.TI.VoiceTranscription.Ai.Domain.Dtos;
using Hapvida.TI.VoiceTranscription.Ai.Domain.Helpers;
using Hapvida.TI.VoiceTranscription.Ai.Domain.Models.v1;
using Microsoft.Extensions.Logging;
using Microsoft.Extensions.Options;

namespace Hapvida.TI.VoiceTranscription.Ai.Domain.Services.v1;

public class VertexAiService : IVertexAiService
{
    private readonly ILogger<VertexAiService> _logger;
    private readonly VertexSettings _options;
    private readonly IBigQueryService _bigQueryService;
    private readonly IVertexClientWrapper _vertexClientWrapper;

    public VertexAiService(
        ILogger<VertexAiService> logger,
        IOptions<VertexSettings> options,
        IBigQueryService bigQueryService,
        IVertexClientWrapper vertexClientWrapper)
    {
        _logger = logger ?? throw new ArgumentNullException(nameof(logger));
        _options = options?.Value ?? throw new ArgumentNullException(nameof(options));
        _bigQueryService = bigQueryService;
        _vertexClientWrapper = vertexClientWrapper;
    }

    public async Task<(string contents, bool shouldRetry)> SendForTranscriptionAsync(
        string uri,
        GoogleCredential googleCredential,
        string messageId,
        string workId,
        CancellationToken cancellationToken = default)
    {
        if (string.IsNullOrWhiteSpace(uri))
            throw new ArgumentException("Uri do arquivo de √°udio √© obrigat√≥ria.", nameof(uri));

        if (string.IsNullOrWhiteSpace(_options.Endpoint))
        {
            _logger.LogError("Endpoint do Vertex AI n√£o configurado! Consumer n√£o ir√° transcrever! - {Message} - {Worker}", messageId, workId);
            throw new InvalidOperationException("Vertex AI endpoint n√£o configurado.");
        }

        try
        {
            _logger.LogInformation("Buscando token autorizado para iniciar transcri√ß√£o - {Message} - {Worker}", messageId, workId);
            _logger.LogInformation("Iniciando solicita√ß√£o de prompt √† LLM! - {Message} - {Worker}", messageId, workId);

            var (promptText, platform) = await GetPromptAndPlatformAsync(workId, messageId, cancellationToken);

            Content contents = new Content
            {
                Role = "user",
                Parts = new List<Part> { new Part { FileData = new FileData { FileUri = uri, MimeType = "audio/mpeg" } },
                                         new Part { Text = promptText } }
            };

            GenerateContentConfig generationConfig = new GenerateContentConfig
            {
                Temperature = 0.0,
                TopP = 0.95,
                ResponseMimeType = "application/json",
                ThinkingConfig = new ThinkingConfig
                {
                    ThinkingBudget = 0
                },
                ResponseSchema = TranscriptionSchemaResponse.Schema,
                Labels = SourcePlatformHelper.ToLabels(platform)
            };

            GenerateContentResponse? vertexResponse = null;
            try
            {
                vertexResponse = await _vertexClientWrapper.GenerateAsync(_options.Model, contents, generationConfig, googleCredential, _options);
            }
            catch (Google.GenAI.ClientError gex)
            {
                if (gex.StatusCode == 429)
                {
                    _logger.LogError("Erro mapeado na Vertex AI de TooManyRequests. - {Message} - {Worker} - Error : {erro}", messageId, workId, gex.Message);
                    return (string.Empty, true);
                }

                _logger.LogError("Erro mapeado na Vertex AI de Google.GenAI.ClientError. - {Message} - {Worker} - Error : {erro}", messageId, workId, gex.Message);
                return (string.Empty, false);
            }
            catch (Exception ex)
            {
                _logger.LogError("Erro n√£o mapeado na Vertex AI. - {Message} - {Worker} - Error : {erro}", messageId, workId, ex.Message);
                return (string.Empty, false);
            }

            if (vertexResponse?.UsageMetadata is null)
            {
                _logger.LogInformation("UsageMetadata n√£o possui dados! - {Message} - {Worker}", messageId, workId);
            }
            else
            {
                try
                {
                    var tokenData = new TokenData()
                    {
                        PromptTokenCount = vertexResponse.UsageMetadata.PromptTokenCount,
                        CandidatesTokenCount = vertexResponse.UsageMetadata.CandidatesTokenCount,
                        TotalTokenCount = vertexResponse.UsageMetadata.TotalTokenCount,
                        FinishMessage = vertexResponse.Candidates?.FirstOrDefault()?.FinishMessage,
                        FinishReason = vertexResponse.Candidates?.FirstOrDefault()?.FinishReason?.ToString()
                    };

                    await _bigQueryService.InsertUsageMetadataAsync(workId, messageId, tokenData, cancellationToken);
                }
                catch (Exception ex)
                {
                    _logger.LogError("Erro ao tentar inserir UsageMetadata no BigQuery. - {Message} - {Worker} - Error : {erro}", messageId, workId, ex.Message);
                }
            }

            var result = GenerateContentResponseHelper.ValidateAndExtract(vertexResponse);

            if (!result.IsValid)
            {
                _logger.LogError(result.Error);
                return (string.Empty, false);
            }

            var response = (result.ExtractedText ?? string.Empty, false);

            _logger.LogInformation("LLM retornou dados! - {Message} - {Worker}", messageId, workId);

            return response;
        }
        catch (Exception ex)
        {
            _logger.LogError("Erro na Vertex AI. - {Message} - {Worker} - Error : {erro}", messageId, workId, ex.Message);
            return (string.Empty, false);
        }
    }

    private async Task<(string promptText, SourcePlatform platform)> GetPromptAndPlatformAsync(string workId, string messageId, CancellationToken cancellationToken)
    {
        try
        {
            _logger.LogInformation("Buscando prompt e platform para workId: {WorkId} - {Message}", workId, messageId);

            var promptDto = await _bigQueryService.GetPromptByWorkIdAsync(workId, cancellationToken);

            if (promptDto is null)
            {
                _logger.LogInformation("Dados n√£o encontrados para workId: {WorkId}, usando valores padr√£o - {Message}", workId, messageId);
                return (PromptDefault.Anamnesis, SourcePlatform.Telehealth);
            }

            var promptText = string.IsNullOrWhiteSpace(promptDto.Prompt) 
                ? PromptDefault.Anamnesis 
                : promptDto.Prompt;

            var platform = SourcePlatformHelper.FromString(promptDto.Platform);

            _logger.LogInformation("Prompt e platform recuperados com sucesso do BigQuery para workId: {WorkId}, Platform: {Platform} - {Message}", workId, platform, messageId);

            return (promptText, platform);
        }
        catch (Exception ex)
        {
            _logger.LogError("Erro ao buscar dados do BigQuery, usando valores padr√£o - {Message} - {WorkId} - Error: {Error}", messageId, workId, ex.Message);
            return (PromptDefault.Anamnesis, SourcePlatform.Telehealth);
        }
    }
}
